import { Component, OnInit, HostListener } from '@angular/core';
import { Router } from '@angular/router';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css']
})
export class HomeComponent implements OnInit {
  constructor(private router: Router) { }

  ngOnInit() {
  }

  navigateToProducts(): void {
    this.router.navigate(['/products']);
  }

  products = [
    { id: 1, name: 'Mountain Bike', price: 299.99, img: 'https://source.unsplash.com/600x400/?mountain,bike', alt: 'Red mountain bike on trail' },
    { id: 2, name: 'Camping Tent', price: 149.99, img: 'https://source.unsplash.com/600x400/?camping,tent', alt: 'Green camping tent pitched in woods' },
    { id: 3, name: 'Hiking Boots', price: 89.99, img: 'https://source.unsplash.com/600x400/?hiking,boots', alt: 'Pair of hiking boots on rock' }
  ];

  // smooth scroll to products section
  scrollToProducts() {
    const el = document.getElementById('products');
    el?.scrollIntoView({ behavior: 'smooth' });
  }

  // track keyboard for focus outlines
  isKeyboard = false;
  @HostListener('window:keydown.tab') onKeyDown() { this.isKeyboard = true; }
  @HostListener('window:mousedown') onMouseDown() { this.isKeyboard = false; }
}
