import { Component } from '@angular/core';
import { Router } from '@angular/router';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css']
})
export class HomeComponent {
  isDropdownOpen = false;
  selectedOption = '';

  constructor(private router: Router) {}

  navigateToProducts(): void {
    this.router.navigate(['/products']);
  }

  toggleDropdown(): void {
    this.isDropdownOpen = !this.isDropdownOpen;
  }

  selectOption(option: string): void {
    this.selectedOption = option;
    this.isDropdownOpen = false;
    // Announce selection to screen readers
    const announcement = document.createElement('div');
    announcement.setAttribute('aria-live', 'polite');
    announcement.textContent = `Selected option: ${option}`;
    document.body.appendChild(announcement);
    setTimeout(() => document.body.removeChild(announcement), 1000);
  }

  // Close dropdown when clicking outside
  onDocumentClick(event: MouseEvent): void {
    if (!(event.target as HTMLElement).closest('.custom-dropdown')) {
      this.isDropdownOpen = false;
    }
  }
}
<section class="home-section">
  <h1>Welcome to Our Accessible Website</h1>

  <div class="feature-section" role="region" aria-labelledby="features-heading">
    <h2 id="features-heading">Key Features</h2>
    <div class="features-grid">
      <div class="feature-card" tabindex="0">
        <h3>Accessible Navigation</h3>
        <p>Fully keyboard-accessible navigation with ARIA labels and skip links.</p>
      </div>
      <div class="feature-card" tabindex="0">
        <h3>Semantic HTML</h3>
        <p>Properly structured content using semantic HTML elements.</p>
      </div>
    </div>
  </div>

  <div class="cta-section" role="region" aria-labelledby="cta-heading">
    <h2 id="cta-heading">Get Started</h2>
    <button class="cta-button" 
            (click)="navigateToProducts()" 
            aria-label="View our products">
      View Products
    </button>
  </div>

  <section class="dropdown-demo" aria-labelledby="dropdown-heading">
    <h2 id="dropdown-heading">Interactive Dropdown Example</h2>
    <div class="custom-dropdown">
      <button 
        [attr.aria-expanded]="isDropdownOpen"
        aria-controls="dropdown-menu"
        (click)="toggleDropdown()"
        class="dropdown-trigger">
        Select an Option
      </button>
      <ul 
        id="dropdown-menu"
        class="dropdown-menu"
        role="listbox"
        [class.open]="isDropdownOpen"
        aria-label="Available options">
        <li role="option" tabindex="0" (click)="selectOption('Option 1')" (keydown.enter)="selectOption('Option 1')">Option 1</li>
        <li role="option" tabindex="0" (click)="selectOption('Option 2')" (keydown.enter)="selectOption('Option 2')">Option 2</li>
        <li role="option" tabindex="0" (click)="selectOption('Option 3')" (keydown.enter)="selectOption('Option 3')">Option 3</li>
      </ul>
    </div>
  </section>
</section>
