import { Component, OnInit, HostListener } from '@angular/core';
import { Router } from '@angular/router';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css']
})
export class HomeComponent implements OnInit {
  isDropdownOpen = false;
  selectedOption = '';
  currentAnnouncement = 'Welcome to our accessible website! Press "/" to use quick search.';

  constructor(private router: Router) {}

  ngOnInit() {
    // Show initial announcement for 5 seconds
    setTimeout(() => {
      if (this.currentAnnouncement.includes('Welcome')) {
        this.currentAnnouncement = '';
      }
    }, 5000);
  }

  @HostListener('window:keydown', ['$event'])
  handleKeyboardEvent(event: KeyboardEvent) {
    // Quick search shortcut
    if (event.key === '/' && !this.isDropdownOpen) {
      event.preventDefault();
      this.currentAnnouncement = 'Quick search activated';
      // Implement quick search functionality here
    }

    // Escape key handling
    if (event.key === 'Escape') {
      if (this.isDropdownOpen) {
        this.toggleDropdown();
      }
      if (this.currentAnnouncement) {
        this.dismissAnnouncement();
      }
    }
  }

  dismissAnnouncement(): void {
    this.currentAnnouncement = '';
  }

  navigateToProducts(): void {
    this.router.navigate(['/products']);
  }

  toggleDropdown(): void {
    this.isDropdownOpen = !this.isDropdownOpen;
    // Announce to screen readers
    this.announceToScreenReader(
      this.isDropdownOpen ? 'Dropdown menu opened' : 'Dropdown menu closed'
    );
  }

  selectOption(option: string): void {
    this.selectedOption = option;
    this.isDropdownOpen = false;
    this.announceToScreenReader(`Selected option: ${option}`);
  }

  private announceToScreenReader(message: string): void {
    const announcement = document.createElement('div');
    announcement.setAttribute('aria-live', 'polite');
    announcement.setAttribute('class', 'sr-only');
    announcement.textContent = message;
    document.body.appendChild(announcement);
    setTimeout(() => document.body.removeChild(announcement), 1000);
  }

  // Close dropdown when clicking outside
  onDocumentClick(event: MouseEvent): void {
    if (!(event.target as HTMLElement).closest('.custom-dropdown')) {
      this.isDropdownOpen = false;
    }
  }
}
<section class="home-section">
  <h1>Welcome to Our Accessible Website</h1>

  <div class="feature-section" role="region" aria-labelledby="features-heading">
    <h2 id="features-heading">Key Features</h2>
    <div class="features-grid">
      <div class="feature-card" tabindex="0">
        <h3>Accessible Navigation</h3>
        <p>Fully keyboard-accessible navigation with ARIA labels and skip links.</p>
      </div>
      <div class="feature-card" tabindex="0">
        <h3>Semantic HTML</h3>
        <p>Properly structured content using semantic HTML elements.</p>
      </div>
    </div>
  </div>

  <div class="cta-section" role="region" aria-labelledby="cta-heading">
    <h2 id="cta-heading">Get Started</h2>
    <button class="cta-button" 
            (click)="navigateToProducts()" 
            aria-label="View our products">
      View Products
    </button>
  </div>

  <section class="dropdown-demo" aria-labelledby="dropdown-heading">
    <div class="custom-dropdown">
      <button 
        [attr.aria-expanded]="isDropdownOpen"
        aria-controls="dropdown-menu"
        (click)="toggleDropdown()"
        class="dropdown-trigger">
        Select an Option
      </button>
      <ul 
        id="dropdown-menu"
        class="dropdown-menu"
        role="listbox"
        [class.open]="isDropdownOpen"
        aria-label="Available options">
        <li role="option" tabindex="0" (click)="selectOption('Option 1')" (keydown.enter)="selectOption('Option 1')">Option 1</li>
        <li role="option" tabindex="0" (click)="selectOption('Option 2')" (keydown.enter)="selectOption('Option 2')">Option 2</li>
        <li role="option" tabindex="0" (click)="selectOption('Option 3')" (keydown.enter)="selectOption('Option 3')">Option 3</li>
      </ul>
    </div>
  </section>

  <main id="main-content" class="home-section">

    <section class="accessibility-features" role="region" aria-labelledby="a11y-heading">
      <h2 id="a11y-heading">Accessibility Features</h2>
      <div class="feature-list">
        <div class="feature-item" tabindex="0">
          <span class="feature-icon" aria-hidden="true">⌨️</span>
          <h3>Full Keyboard Navigation</h3>
          <p>Navigate our entire site using just your keyboard with intuitive shortcuts and visible focus indicators.</p>
        </div>
        <div class="feature-item" tabindex="0">
          <span class="feature-icon" aria-hidden="true">🔍</span>
          <h3>Screen Reader Support</h3>
          <p>Compatible with popular screen readers and includes detailed ARIA labels and live announcements.</p>
        </div>
        <div class="feature-item" tabindex="0">
          <span class="feature-icon" aria-hidden="true">🎨</span>
          <h3>High Contrast Mode</h3>
          <p>Automatically adapts to your system's high contrast settings for better visibility.</p>
        </div>
      </div>
    </section>

    <div class="keyboard-shortcuts" role="complementary" aria-labelledby="shortcuts-heading">
      <h2 id="shortcuts-heading" tabindex="0">Keyboard Shortcuts</h2>
      <div class="shortcuts-grid" role="list">
        <div class="shortcut-item" role="listitem" tabindex="0">
          <kbd>Tab</kbd>
          <span>Navigate between elements</span>
        </div>
        <div class="shortcut-item" role="listitem" tabindex="0">
          <kbd>Enter</kbd>
          <span>Activate buttons and links</span>
        </div>
        <div class="shortcut-item" role="listitem" tabindex="0">
          <kbd>Esc</kbd>
          <span>Close dialogs and menus</span>
        </div>
