import { Component } from '@angular/core';
import { Router } from '@angular/router';

interface CartItem {
  id: string;
  name: string;
  price: number;
  quantity: number;
  image: string;
}

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.scss']
})
export class HomeComponent {
  isDropdownOpen = false;
  selectedOption = '';
  cartItems: CartItem[] = [];

  constructor(private router: Router) {}

  get cartTotal(): number {
    return this.cartItems.reduce((total, item) => total + (item.price * item.quantity), 0);
  }

  addToCart(productId: string): void {
    const productMap = {
      keyboard: {
        id: 'keyboard',
        name: 'Ergonomic Keyboard',
        price: 89,
        image: 'assets/images/product1.jpg'
      },
      mouse: {
        id: 'mouse',
        name: 'Adaptive Mouse',
        price: 65,
        image: 'assets/images/product2.jpg'
      }
    };

    const product = productMap[productId];
    const existingItem = this.cartItems.find(item => item.id === productId);

    if (existingItem) {
      existingItem.quantity += 1;
    } else {
      this.cartItems.push({
        ...product,
        quantity: 1
      });
    }

    // Announce to screen readers
    this.announceToScreenReader(`Added ${product.name} to cart`);
  }

  removeFromCart(item: CartItem): void {
    const index = this.cartItems.indexOf(item);
    if (index > -1) {
      this.cartItems.splice(index, 1);
      this.announceToScreenReader(`Removed ${item.name} from cart`);
    }
  }

  increaseQuantity(item: CartItem): void {
    item.quantity += 1;
    this.announceToScreenReader(`Increased quantity of ${item.name} to ${item.quantity}`);
  }

  decreaseQuantity(item: CartItem): void {
    if (item.quantity > 1) {
      item.quantity -= 1;
      this.announceToScreenReader(`Decreased quantity of ${item.name} to ${item.quantity}`);
    }
  }

  checkout(): void {
    // Implement checkout logic
    alert('Proceeding to checkout...');
  }

  navigateToProducts(): void {
    this.router.navigate(['/products']);
  }

  toggleDropdown(): void {
    this.isDropdownOpen = !this.isDropdownOpen;
  }

  selectOption(option: string): void {
    this.selectedOption = option;
    this.isDropdownOpen = false;
    // Announce selection to screen readers
    const announcement = document.createElement('div');
    announcement.setAttribute('aria-live', 'polite');
    announcement.textContent = `Selected option: ${option}`;
    document.body.appendChild(announcement);
    setTimeout(() => document.body.removeChild(announcement), 1000);
  }

  private announceToScreenReader(message: string): void {
    const announcement = document.createElement('div');
    announcement.setAttribute('aria-live', 'polite');
    announcement.setAttribute('class', 'sr-only');
    announcement.textContent = message;
    document.body.appendChild(announcement);
    setTimeout(() => document.body.removeChild(announcement), 1000);
  }

  // Close dropdown when clicking outside
  onDocumentClick(event: MouseEvent): void {
    if (!(event.target as HTMLElement).closest('.custom-dropdown')) {
      this.isDropdownOpen = false;
    }
  }
}
